import React, { useEffect } from "react";
import { Helmet } from "react-helmet";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";
import AllProjects from "../components/projects/allProjects";

import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/projects.css";

const Projects = () => {
	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const currentSEO = SEO.find((item) => item.page === "projects");

	return (
		<React.Fragment>
			<Helmet>
				<title>{`Projects | ${INFO.main.title}`}</title>
				<meta name="description" content={currentSEO.description} />
				<meta
					name="keywords"
					content={currentSEO.keywords.join(", ")}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="projects" />
				<div className="content-wrapper">
					<div className="projects-logo-container">
						<div className="projects-logo">
							<Logo width={46} />
						</div>
					</div>
					<div className="projects-container">
						<div className="title projects-title">
							Projects That Shaped My Journey
						</div>

						<div className="subtitle projects-subtitle">
							Throughout my career, I've had the opportunity to work on a wide range of impactful projects, particularly in large-scale government initiatives. Many of the projects I contributed to were in-house, focused on areas like water canals and irrigation systems, and were primarily tailored for governmental use, so they remain unpublished. However, there are a few projects I’ve been part of that are publicly accessible. These projects reflect my evolution as a programmer and are an ongoing source of learning. I invite you to explore the code, share insights, and collaborate for further improvements. It’s always inspiring to exchange ideas and grow through collaboration.
						</div>

						<div className="projects-list">
							<AllProjects />
						</div>
					</div>
					<div className="page-footer">
						<Footer />
					</div>
				</div>
			</div>
		</React.Fragment>
	);
};

export default Projects;
